mapper.select:
select *
from swiper
where qid = #{qid} and category = 'A';

mapper.update:
    update enterprise
	set name=#{name}, introduction=#{introduction},jczs=#{jczs}
	where qid=#{qid}

update enterprise
set videopath=null
where qid=#{qid}

update enterprise
set videopath=#{videopath}
where qid=#{qid}

mapper.select:
select jczs
from enterprise
where qid=#{qid}

select aid, branch, address, tel, isDeleted
from address
where qid = #{qid};

select branch, address, tel, longitude, latitude
from address
where qid = #{qid} and aid=#{aid};

mapper.update:
update address
set branch=#{branch},address=#{address},tel=#{tel},longitude=#{longitude},latitude=#{latitude}
where qid=#{qid} and aid=#{aid};

update address
set isDeleted = 1
where qid=#{qid} and aid=#{aid};

mapper.select:
select tid, tname, tphoto, introduction
from teacher
where qid=#{qid };

 	    select *
		from (
		select @rownum:=@rownum+1 as rownum, a.* 
			  from (select tid, tname, tphoto, introduction
		            from teacher
		            where qid = #{qid}
		            )a,
		            (select @rownum :=0)t
		)b
			 where b.rownum >=#{startNum} and b.rownum <=#{endNum};

select tname,tphoto, introduction
from teacher
where qid=#{qid} and tid=#{tid};

mapper.update:
update teacher
set tphoto = null
where qid=#{qid} and tid=#{tid};

update teacher
set tphoto = #{tphoto}
where qid=#{qid} and tid=#{tid};

update teacher
set tname=#{tname}, introduction=#{introduction}
where qid=#{qid} and tid=#{tid};

mapper.select:
select *
from teacher
where tname like #{tname} and qid=#{qid};

select id, img
from firstpageofteachers
where qid = #{qid};
